(function calculatedFieldValue(current) {

    // Add your code here
    var u_ver = current.u_ver;
    if (u_ver) {
        var str_year = u_ver.substring(0, 4);
        var str_month = u_ver.substring(4);
        if (parseInt(str_month) >= 4) {
            return parseInt(str_year);
        } else {
            return parseInt(str_year) - 1;
        }

    } else {
        return null; // return the calculated value
    }

})(current);

