    }

})(current);

(function calculatedFieldValue(current) {

    // Add your code here
    var u_ver = current.u_ver;
    if (u_ver) {
        var str_month = u_ver.substring(4);
        var int_month = parseInt(str_month);
        if (int_month >= 4) {
            return parseInt(str_month) - 3;
        } else {
			return parseInt(str_month) + 9;
		}
    } else {
        return null; // return the calculated value
    }

})(current);
