element.calculation:
(function calculatedFieldValue(current) {

	var dt = current.date;
	if (!dt.nil()) {
		//return dt.getDisplayValue();
		var gtd = new GlideDateTime(dt.getDisplayValue());
		//gtd.setDisplayValue(dt.getDisplayValue() + " 00:00:00");
		gtd.addDaysUTC(-1);
		return gtd.getWeekOfYearUTC();
	}
	return null;  // return the calculated value

})(current);

