(function calculatedFieldValue(current) {

	// Add your code here
    var u_ver = current.u_version;
    if (u_ver) {
        var year = parseInt(u_ver.substring(0, 4));
        var month = parseInt(u_ver.substring(5));
        if (month >= 4) {
            return year;
        } else {
            return year - 1;
        }
    } else {
        return null; // return the calculated value
    }

})(current);

