(function calculatedFieldValue(current) {

	// Add your code here
    var u_date = current.u_date;
    if (u_date) {
        var d = u_date.getDisplayValue();
		var month = parseInt(d.substring(5, 7));
        if (month >= 4) {
            return month - 3;
        } else {
			return month + 9;
		}
    } else {
        return null; // return the calculated value
    }

})(current);

