(function calculatedFieldValue(current) {

	// Add your code here
    var u_ver = current.u_version;
    if (u_ver) {
        var date = u_ver + '-01';
        var simpleDateFormat = 'yyyy-MM-dd';
        var gdt = new GlideDateTime();
        gdt.setDisplayValue(date, simpleDateFormat);
        return gdt.getLocalDate();
    } else {
        return null; // return the calculated value
    }

})(current);

