sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var sourceTable = "sys_attachment";
	var targetTable = current.table_name.replace("ZZ_YY", "");
	if (current.operation() == 'insert') {
		gs.info(">>>Debug: Insert Attachment table_name=" + current.table_name +
			", table_sys_id=" + current.table_sys_id);
		var gr1 = new GlideRecord(targetTable);
		if (gr1.get(('sys_id', current.table_sys_id))) {
			gr1.setValue('has_attachment', 'o');
			gr1.update();
		} else {
			gs.error(">>>Error: update Attachment flag, not found table_sys_id");
		}

	} else if (current.operation() == 'delete') {
		gs.info(">>>Debug: Delete Attachment table_name=" + current.table_name +
			", table_sys_id=" + current.table_sys_id);
		var gr = new GlideRecord(sourceTable);
		gr.addQuery('table_name', current.table_name);
		gr.addQuery('table_sys_id', current.table_sys_id);
		gr.query();
		gs.info(">>>Debug: Attachment count=" + gr.getRowCount());
		//没有附件的场合
		if (gr.getRowCount() == 0) {
			var gr2 = new GlideRecord(targetTable);
			if (gr2.get(('sys_id', current.table_sys_id))) {
				gr2.setValue('has_attachment', '');
				gr2.update();
			} else {
				gs.error(">>>Error: update Attachment flag, not found table_sys_id");
			}
		}
	}

})(current, previous);
