sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var targetTable = "x_58872_needit_d1";
	var sourceTable = "x_58872_needit_d1_ver";

	var grSource = null, grTarget = null, grInsert = null;
	var current_u_date = current.getValue('u_date');
	var current_u_company = current.getValue('u_company');
	var current_u_item = current.getValue('u_item');
	var current_u_version = current.getValue('u_version');

	if (current.operation() == 'insert' || current.operation() == 'update') {
		if (current.u_ative) {
			grSource = current;
		} else {
			//非Active状态下退避
			return;
		}
	} else if (current.operation() == 'delete') {
		//获取更早版本的情报
		grSource = new GlideRecord(sourceTable);
		grSource.addQuery('u_date', current_u_date);
		grSource.addQuery('u_company', current_u_company);
		grSource.addQuery('u_item', current_u_item);
		grSource.addQuery('u_version', '<', current_u_version);
		grSource.orderByDesc('u_version');
		grSource.setLimit(1);
		grSource.query();
		if (!grSource.next()) grSource = null;
	}
	//更新目标表
	if (grSource != null) {
		grTarget = new GlideRecord(targetTable);
		grTarget.addQuery('u_date', grSource.getValue('u_date'));
		grTarget.addQuery('u_company', grSource.getValue('u_company'));
		grTarget.addQuery('u_item', grSource.getValue('u_item'));
		grTarget.query();
		if (grTarget.next()) { //存在相同供应商，机种
			if (grTarget.u_version.nil() || grSource.u_version >= grTarget.u_version || 
				(current.operation() == 'delete' && current.u_version == grTarget.u_version)) { 
				grTarget.setValue('u_p', grSource.getValue('u_p'));
				grTarget.setValue('u_s', grSource.getValue('u_s'));
				grTarget.setValue('u_i', grSource.getValue('u_i'));
				grTarget.setValue('u_version', grSource.getValue('u_version'));
				grTarget.update();
			}
		} else { //新规供应商，机种
			grInsert = new GlideRecord(targetTable);
			grInsert.initialize();
			grInsert.setValue('u_date', grSource.getValue('u_date'));
			grInsert.setValue('u_company', grSource.getValue('u_company'));
			grInsert.setValue('u_item', grSource.getValue('u_item'));
			grInsert.setValue('u_p', grSource.getValue('u_p'));
			grInsert.setValue('u_s', grSource.getValue('u_s'));
			grInsert.setValue('u_i', grSource.getValue('u_i'));
			grInsert.setValue('u_version', grSource.getValue('u_version'));
			grInsert.insert();
		}
	} else {
		//删除的场合，且找不到前版本的数据时，同步删除最新数据
		new global.GlideQuery(targetTable)
			.where('u_date', current_u_date)
			.where('u_company', current_u_company)
			.where('u_item', current_u_item)
			.deleteMultiple();
	}

})(current, previous);
