sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var targetTable = "x_58872_needit_d1";
	if (current.operation() == 'insert' || current.operation() == 'update') {
		var gr1 = new GlideRecord(targetTable);
		gr1.addQuery('u_date', current.getValue('u_date'));
		gr1.addQuery('u_company', current.getValue('u_company'));
		gr1.addQuery('u_item', current.getValue('u_item'));
		gr1.query();
		if (gr1.next()) { //存在相同供应商，机种
			if (gr1.u_version.nil() || current.u_version >= gr1.u_version) {
				gr1.setValue('u_p', current.getValue('u_p'));
				gr1.setValue('u_s', current.getValue('u_s'));
				gr1.setValue('u_i', current.getValue('u_i'));
				gr1.setValue('u_version', current.getValue('u_version'));
				gr1.update();
			}
		} else { //新规供应商，机种
			var grInsert = new GlideRecord(targetTable);
			grInsert.initialize();
			grInsert.setValue('u_date', current.getValue('u_date'));
			grInsert.setValue('u_company', current.getValue('u_company'));
			grInsert.setValue('u_item', current.getValue('u_item'));
			grInsert.setValue('u_p', current.getValue('u_p'));
			grInsert.setValue('u_s', current.getValue('u_s'));
			grInsert.setValue('u_i', current.getValue('u_i'));
			grInsert.setValue('u_version', current.getValue('u_version'));
			grInsert.insert();
		}
	} else if (current.operation() == 'delete') {
		//
	}

})(current, previous);
