sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var year = null, company = null, item = null;
	var max_ver = null;

	if (current.operation() == 'update' && 
		current.getValue('ver') == previous.getValue('ver')) {
		return;
	}
	year = current.getValue('year');
	company = current.getValue('company');
	item = current.getValue('item');
	gs.info(current.operation() + ' x_58872_needit_u0, year=' + year + ', company=' + company + ', item=' + item);

	var ga = new GlideAggregate('x_58872_needit_u0');
	ga.addQuery('year', year);
	ga.addQuery('company', company);
	ga.addQuery('item', item);
	ga.groupBy('year');
	ga.groupBy('company');
	ga.groupBy('item');
	ga.addAggregate('MAX', 'ver');
	ga.query();
	if (ga.next()) {
		max_ver = ga.getAggregate('MAX', 'ver');
		gs.info('max_ver=' + max_ver);
	} else {
		gs.info('max_ver=null');
	}

	var gr = new GlideRecord('x_58872_needit_vu0');
	gr.addQuery('year', year);
	gr.addQuery('company', company);
	gr.addQuery('item', item);
	gr.query();
	if (gr.next()) {
		if (max_ver) {
			gr.setValue('ver', max_ver);
			gr.update();
		} else {
			gr.deleteRecord();
		}
	} else {
		if (max_ver) {
			gr.initialize();
			gr.setValue('year', year);
			gr.setValue('company', company);
			gr.setValue('item', item);
			gr.setValue('ver', max_ver);
			gr.insert();
		}
	}

})(current, previous);
