sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   // Instantiate the GetEmailAddress Script Include 
    var getDeptName = new GlideAjax('GetDeptName');
    // Specify the getEmail method
    getDeptName.addParam('sysparm_name','getDeptInfo');
    // Pass the Requested for sys_id
    getDeptName.addParam('sysparm_deptcd', g_form.getValue('dept_cd'));
    // Send the request to the server
    getDeptName.getXML(populateDeptNameField);


    // When the response is back from the server
    function populateDeptNameField(response){
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var deptNameFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('dept_name');
        g_form.setValue('dept_name',deptNameFromScriptInclude);
    }
}
