sys_script_include.script:
var GetDeptName = Class.create();
GetDeptName.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// Define the getEmail function.  
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record
	getDeptInfo: function() {
		var deptRecord = new GlideRecord("x_58872_needit_dept");
		deptRecord.get(this.getParameter('sysparm_deptcd'));
		return deptRecord.dept_name + '';
	},
    type: 'GetDeptName'
});
