sys_script_include.script:
var GetAttachmentId = Class.create();
GetAttachmentId.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getFromTable: function() {
		//返回值
		var response = {};
		var result = ""; //执行结果ok，ng或者err
		var message = ""; //ng或者err的时候设置异常原因
		//自定义的返回值
		var id = "";
		var ret = false;

		//检索条件
		var p_table_name = this.getParameter("sysparm_table_name");
		var p_record_id = this.getParameter("sysparm_record_id");
		var p_file_name = this.getParameter("sysparm_file_name");
		gs.info("GetAttachmentId: " +
			", p_table_name=" + p_table_name +
			", p_record_id=" + p_record_id);

		var attachment = new x_58872_needit.AttachmentId(p_table_name);
		//设定模板文件名的条件
		if (!(typeof p_file_name == "undefined" || p_file_name == null)) {
			ret = attachment.getByFilename(p_file_name);
		}
		//设定模板配置记录ID的条件
		if (!(typeof p_record_id == "undefined" || p_record_id == null)) {
			ret = attachment.getByRecordId(p_record_id);
		}

		if (!ret) {
			result = "ng";
			message = attachment.message;
		} else {
			result = "ok";
			id = attachment.id;
		}
		response["result"] = result;
		response["message"] = message;
		response["id"] = id;
		return(JSON.stringify(response));
	},
    type: 'GetAttachmentId'
});
