sys_script_include.script:
var RefreshPsiChange = Class.create();
RefreshPsiChange.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	executeNow: function() {
		var job_name = "Auto Fetch Change Date";
		var gr = new GlideRecordSecure("sysauto_script");
		gr.addQuery("name", job_name);
		gr.query();
		if (gr.next()) {
			gs.executeNow(gr);
			gs.info(job_name + ":Successfully executed the Scheduled Job");
		} else {
			gs.error(job_name + ":Failed to find the Scheduled Job");
		}
		gr = null;
		return('done');
	},

	collect: function() {
		//gs.addInfoMessage('Receive a refresh request, please wait for screen refresh');
		var type = this.getParameter("sysparm_type");
		if (!type) {
			type = 'all';
		}
		var job = new x_58872_needit.CollectPsiChange();
		job.collect(type);
		//gs.addInfoMessage(type + ' data is up to date now');

		var response = {};
		response["result"] = "ok";
		var records = [];

		var trans = new x_58872_needit.Transform();
		var gr = new GlideRecordSecure('x_58872_needit_u0');
		gr.query();
		while (gr.next()) {
			var obj = {};
			obj.item = trans.toString(gr.item);
			obj.company = trans.toString(gr.company);
			obj.p4 = trans.getValue(gr.p4);
			obj.p5 = trans.getValue(gr.p5);
			obj.s4 = trans.getValue(gr.s4);
			obj.s5 = trans.getValue(gr.s5);
			records.push(obj);
		}
		response["records"] = records;
		gr = null;

		//return(type + ' is done');
		return(JSON.stringify(response));
	},
    type: 'RefreshPsiChange'
});
