sys_script_include.script:
var GetCalendar = Class.create();
GetCalendar.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//获取这星期的日历
	getThisWeek: function() {
		//返回值
		var response = {};
		var result = ""; //执行结果ok，ng或者err
		var message = ""; //ng或者err的时候设置异常原因
		//自定义的返回值
		var datas = null;
		var ret = false;

		var calendar = new x_58872_needit.MyCalendar();
		var today = calendar.getToday();
		ret = calendar.getWeek(today.year, today.week_of_year);
		if (!ret) {
			result = "ng";
			message = calendar.message;
		} else {
			result = "ok";
			datas = calendar.datas;
		}
		response["result"] = result;
		response["message"] = message;
		response["datas"] = datas;
		return(JSON.stringify(response));
	},

	//获取这星期和下星期的日历
	getThisNextWeek: function() {
		//返回值
		var response = {};
		var result = ""; //执行结果ok，ng或者err
		var message = ""; //ng或者err的时候设置异常原因
		//自定义的返回值
		var datas = null;
		var ret = false;

		var calendar = new x_58872_needit.MyCalendar();
		var today = calendar.getToday();

		//获取下星期数据
		var thisWeek = null;
		ret = calendar.getWeek(today.year, today.week_of_year);
		if (ret) {
			thisWeek = calendar.datas;
		}

		//获取这星期数据
		var nextWeek = null;
		if (ret) {
			ret = calendar.getWeek(today.year, today.week_of_year+1);
			if (ret) {
				nextWeek = calendar.datas;
			}
		}

		if (!ret) {
			result = "ng";
			message = calendar.message;
		} else {
			result = "ok";
			datas = thisWeek.concat(nextWeek);
		}
		response["result"] = result;
		response["message"] = message;
		response["datas"] = datas;
		return(JSON.stringify(response));
	},

	//获取这月的日历
	getThisMonth: function() {
		//返回值
		var response = {};
		var result = ""; //执行结果ok，ng或者err
		var message = ""; //ng或者err的时候设置异常原因
		//自定义的返回值
		var datas = null;
		var ret = false;

		var calendar = new x_58872_needit.MyCalendar();
		var today = calendar.getToday();
		ret = calendar.getMonth(today.year, today.month);
		if (!ret) {
			result = "ng";
			message = calendar.message;
		} else {
			result = "ok";
			datas = calendar.datas;
		}
		response["result"] = result;
		response["message"] = message;
		response["datas"] = datas;
		return(JSON.stringify(response));
	},

    type: 'GetCalendar'
});
