sys_transform_script.script:
(function runTransformScript(source, import_set, map, log, target /*undefined onStart*/ ) {

    // Add your code here
    if (!error) {
        var set = import_set.number;

        var query = new global.GlideQuery('x_58872_needit_a0')
            .where('set', set)
            .groupBy('company')
            .aggregate('count', 'item_count')
            .select()
            .forEach(function(priority) {
                //获取公司名
                var company = priority.group.company;

                //删除老数据
                var gr1 = new GlideRecord('x_58872_needit_a0');
                gr1.addQuery('company', company);
				gr1.addQuery('active', '1');
                var condition = gr1.addQuery('set', '<', set);
                condition.addOrCondition('set', '=', '');
                gr1.query();
                //gr1.deleteMultiple();
                while (gr1.next()) {
                    gr1.active = '0';
                    gr1.update();
                }

                var gr2 = new GlideRecord('x_58872_needit_t1');
                gr2.setValue('set', set);
                gr2.setValue('company', company);
                gr2.setValue('memo', 'on group');
                gr2.insert();
            });

        var gr = new GlideRecord('x_58872_needit_t1');
        gr.setValue('set', set);
        gr.setValue('memo', 'on complete');
        gr.insert();
    }

})(source, import_set, map, log, target);
