sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	// Add your code here
	var row = source.sys_import_row;
	var checkItemLabel = 'Ver';
	var checkVal = source.u_ver; //put date value here
	// regular expression for yyyy-mm-dd
	//var regexp = /^(19|20)\d\d[-](0[1-9]|1[012])[-](0[1-9]|[12][0-9]|3[01])$/;
	// regular expression for yyyymm
	var regexp = /^(19|20)\d\d(0[1-9]|1[012])$/;
	if (!regexp.test(checkVal)) {
		ignore = true;
		log.error(checkItemLabel + '(' + checkVal + ') is Invalid in Row ' + row);
	}

})(source, map, log, target);
