sys_ui_action.script:
var onClick = function() {
	var params = {
		"record_id": g_form.getUniqueValue(),
		"invoice_no": g_form.getValue('invoice_no')
	};
	getAjax('x_58872_needit.RefreshPackingList', 'refresh', params)
		.then(function(data) {
			//alert(JSON.stringify(data.datas));
			alert("执行成功");
			//延迟刷新画面
			setTimeout(function() {
				location.reload();
			}, 500);
		})
		.catch(function(data) {
			alert("执行失败："+data.message);
		});
};

// 纯js方法，不需要引入外部包
// getAjax函数：通过axios从指定Ajax函数返回结果给Promise
var getAjax = function(url, functionName, params) {
	//设定默认params为空
	if (typeof params == 'undefined' || params == null) {
		params = {};
	}

	return new Promise(function(resolve, reject) {
		var ga = new GlideAjax(url);
		ga.addParam('sysparm_name', functionName);
		for (var key in params) {
			ga.addParam('sysparm_' + key, params[key]);
		}
		ga.getXMLAnswer(function(answer) {
			var jsonObj = JSON.parse(answer);
			if (jsonObj.result == "ok") {
				resolve(jsonObj);
			} else {
				reject(jsonObj);
			}
		});
	});
};
