// getFile函数：通过axios从指定URL下载文件并返回Promise
// 可以指定的文件类型有'json'，'arraybuffer'，'text'，'blob'
var getFile = function(url, type) {
	//设定默认type为blob
	if (typeof type == 'undefined' || type == null) {
		type = "blob";
	}
	return new Promise(function(resolve, reject) {
		axios({
			method: 'get',
			url: url,
			responseType: type
		}).then(function(data) {
			resolve(data.data);
		}).catch(function(error) {
			reject(error);
		});
	});
};

