$j('#transform1Input').change(function() {
	var name = $j('#transform1Input').prop('value');
	//未选文件的时候不处理
	if (name == "") {
		return;
	}
	var fileObj = $j('#transform1Input').prop('files')[0];
	// 处理所选择的文件
	var params = {
		"common_heads": ["機種", "販社", "仕向地", "仕向港", "PSI"], 
		"common_head_types": ["string", "string", "string", "string", "string"],
		"loop_col_count": 1, 
		"loop_heads": ["数量"], "loop_head_types": ["number"], 
		"level": 1, "loop_levels": [
			{"row": 1, "loop_col_starts": [1], "loop_col_spans": [1], 
			"level_titles": ["日期"], "level_title_types": ["date"]}
		]
	};
	excelUtility.parseLongToShort(fileObj, params)
		.then(function(datas) {
			csvUtility.saveCsv(datas.details, "文件导出测试3.csv");
		}).catch(function(message) {
			alert(message);
		});

	//清空原选择框是为了更好的触发onChang事件
	$j('#transform1Input').val("");
});

$j('#transform2').click(function() {
	$j('#transform2Input').click();
});

$j('#transform2Input').change(function() {
	var name = $j('#transform2Input').prop('value');
	//未选文件的时候不处理
	if (name == "") {
		return;
	}
	var fileObj = $j('#transform2Input').prop('files')[0];
	// 处理所选择的文件
	var reader = new FileReader();
	/* read Text */
	reader.readAsArrayBuffer(fileObj);
	reader.onload = function() {
		//清空原选择框是为了更好的触发onChang事件
		$j('#transform2Input').val("");

		var workbook = new ExcelJS.Workbook();
		//var utility = x_58872_needit.Utility;
		workbook.xlsx.load(this.result).then(function(data) {
			var worksheet = workbook.getWorksheet(1);
			if (typeof worksheet == "undefined") {
				worksheet = workbook.worksheets[0];
			}

			var firstDataLine = 2;
			var firstDataColumn = 6;
			var csvDatas = [];
			var cols = [""];
			//获取表头
			var headRow = worksheet.getRow(1);
			headRow.eachCell({ includeEmpty: true }, function(cell, colNumber) {
				cols.push(utility.transformString(cell.value));
				// if (colNumber < firstDataColumn) {
				// 	cols.push(cell.text);
				// } else {
				// 	cols.push(utility.dateToString(cell.value));
				// }
			});
			//设置表头
			for (i = 1, len = cols.length; i < len; i++) {
				worksheet.getColumn(i).key = cols[i];
			}
			//获取数据
			try {
				worksheet.eachRow({ includeEmpty: true }, function(row, rowNumber) {
					if (rowNumber >= firstDataLine) {
						var item = row.getCell('機種').text;
						if (item == null || item.trim() == "") {
							throw new Error('touch the end line');
						}
						row.eachCell({ includeEmpty: true }, function(cell, colNumber) {
							if (colNumber >= firstDataColumn) {
								var tmpVal = cell.value;
								var val = null;
								if (tmpVal) {
									val = tmpVal;
								} else {
									val = 0;
								}
								var line = {
									"機種": item,
									"販社": row.getCell('販社').text,
									"仕向地": row.getCell('仕向地').text,
									"仕向港": row.getCell('仕向港').text,
									"PSI": row.getCell('PSI').text,
									"日期": cols[colNumber],
									"数量": val
								};
								csvDatas.push(line);
							}
						});
					}
				});
			} catch (e) {}

			csvUtility.saveCsv(csvDatas, "文件导出测试5.csv");
		}).catch(function(error) {
			alert(error.message);
		});
	};
});

