$j('#change_data').click(function() {
	var d = null;
	var c = null;

	if (currentData == 1) {
		d = data2;
		c = column2;
		currentData = 2;
	} else {
		d = data1;
		c = column1;
		currentData = 1;
	}
	$j("#jsGrid").jsGrid({
		width: "100%",
		//height: "600px",

		filtering: false,
		editing: false,
		sorting: true,
		paging: true,

		pageSize: 10,
		pageButtonCount: 5,

		data: d,

		fields: c
	});
});
