		"common_head_types": ["string", "string", "string", "string", "string"],
		"loop_col_count": 1, "loop_heads": ["数量"], "loop_head_types": ["number"], 
		"level": 2, "loop_levels": [
			{"row": 1, "loop_col_starts": [1], "loop_col_spans": [7], 
			"level_titles": ["星期"], "level_title_types": ["string"]},
			{"row": 2, "loop_col_starts": [1], "loop_col_spans": [1], 
			"level_titles": ["日期"], "level_title_types": ["date"]}
		]
	};
	// var params = {
	// 	"common_heads": ["機種", "販社", "仕向地", "仕向港", "PSI"], 
	// 	"common_head_types": ["string", "string", "string", "string", "string"],
	// 	"loop_col_count": 7, "loop_heads": ["数量1","数量2","数量3","数量4","数量5","数量6","数量7"],
	// 	"loop_head_types": ["number", "number", "number", "number", "number", "number", "number"], 
	// 	"level": 2, "loop_levels": [
	// 		{"row": 1, "loop_col_starts": [1], "loop_col_spans": [7], 
	// 		"level_titles": ["星期"], "level_title_types": ["string"]}
	// 	]
	// };
	excelUtility.parseLongToShort(fileObj, params)
		.then(function(datas) {
			csvUtility.saveCsv(datas.details, "文件导出测试3.csv");
		}).catch(function(message) {
			alert(message);
		});

	//清空原选择框是为了更好的触发onChang事件
	$j('#transform3Input').val("");
});

$j('#change_data').click(function() {
	var d = null;
	var c = null;

	if (currentData == 1) {
		d = data2;
		c = column2;
		currentData = 2;
	} else {
		d = data1;
		c = column1;
		currentData = 1;
	}
	$j("#jsGrid").jsGrid({
		width: "100%",
		//height: "600px",

		filtering: false,
		editing: false,
		sorting: true,
		paging: true,

		pageSize: 10,
		pageButtonCount: 5,

		data: d,

		fields: c
	});
});

$j('#get_date').click(function() {
	var dt = new GlideDateTime();
	alert(dt.getDisplayValue());
});

$j('#uploadFile').change(function() {
	var name = $j('#uploadFile').prop('value');
	//未选文件的时候不处理
	if (name == "") {
		return;
	}
	var fileObj = $j('#uploadFile').prop('files')[0];
	utility.getLocalFile(fileObj, 'dataURL').then(function(reader) {
		gel('uploadFileName').value = reader.filename;
		gel('upd').value = reader.result;
		//gel('uploadFileType').value = reader.filetype;
		if (reader.filetype == "text/csv") {
			gel('uploadFileType').value = "CSV";
			//解析CSV内容，保存到upd2
			//parseNormalCsv(fileObj, "upd2");
			csvUtility.parseNormalCsv(fileObj, true)
				.then(function(datas) {
					gel('upd2').value = JSON.stringify(datas);
					alert("get " + datas.details.length + " data lines");
				}).catch(function(message) {
					alert(message);
					gel('upd2').value = "";
				});
		} else {
			gel('uploadFileType').value = "Excel";
			// 解析Excel内容，保存到upd2
			// parseNormalExcel(fileObj, "upd2");
			// var params = ["string", "string", "formula", "string", "number", "number", "number", "number"];
			var params = ["string", "string", "string", "string", "number", "number", "number", "number"];
			excelUtility.parseNormalExcel(fileObj, params, true)
				.then(function(datas) {
					gel('upd2').value = JSON.stringify(datas);
					alert("get " + datas.details.length + " data lines");
				}).catch(function(message) {
					alert(message);
					gel('upd2').value = "";
				});
		}
	}).catch(function(reader) {
		gel('upd').value = "";
		gel('upd2').value = "";
		gel('uploadFileName').value = "";
		gel('uploadFileType').value = "";
		alert("文件加载失败");
	});

	//清空原选择框是为了更好的触发onChang事件
	$j('#uploadFile').val("");
});

