sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    //console.log(JSON.stringify(event.payload,null,4));
    //console.log(event.payload.value);
    var type = event.payload.value;
    if (type == 'all') {
        type = '';
    } else {
        type = 'u_request_type='+type;
    }
    console.log(type);
    api.setState('listQuery', type);

}
